import {
    Card,
    CardMedia,
    CardActions,
    CardContent,
    Typography,
    CardActionsArea,
    Button,
} from "@material-ui/core";
import { ShoppingCart } from "@material-ui/core";

const Product = ({product , addProduct = () => {} })=>(
<Card className="custom-card">
<CardActionsArea>
<CardMedia
    component="img"
    alt="product-image"
    height="260"
    className="card-image"
    image={product.media.source}
    title="product-tile"
/>
<CardContent className="content">
    <Typography className="title" gutterBottom variants="h5" components="h2">
        {product.name}
    </Typography>
</CardContent>
</CardActionsArea>
<CardActions className="actions-content">
    <Typography className="price"gutterBottom variants="h5" components="h2">
        {product.price.formatted_with_symbol}
    </Typography>
    <Button
    size="large"
    className="custom-button"
    onClick={()=>{
        addProduct(product.id, 1);
    }}
    >
        <ShoppingCart/>
    </Button>
</CardActions>
</Card>
);

export default Products
